import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const items = [{
        name: 'Cerveja Preta',
        price: 10,
        count: 0

    },
    {
        name: 'Taça de vinho',
        price: 12,
        count: 0

    },
    {
        name: 'Caipirinha',
        price: 13,
        count: 0

    }
]

const state = {
    tables: [{
            id: 1,
            hour: '00:00',
            value: 0,
            payment: [],
            order: {
                products: items
            }


        },
        {
            id: 2,
            hour: '00:00',
            value: 0,
            payment: [],
            order: {
                produtos: items
            }
        },
        {
            id: 3,
            hour: '00:00',
            value: 0,
            payment: [],
            order: {
                produtos: items
            }
        },
        {
            id: 4,
            hour: '00:00',
            value: 0,
            payment: [],
            order: {
                produtos: items
            }
        },
        {
            id: 5,
            hour: '00:00',
            value: 0,
            payment: [],
            order: {
                produtos: items
            }
        },

    ],
}

const mutations = {
    setCount: (state, payload) => {
        const {
            id,
            value,
            hour
        } = payload
        const table = state.tables.find(table => table.id === id)
        table.value = value

        if (table.hour == '00:00')
            table.hour = hour

        if (value == 0)
            table.hour = '00:00'
    },
    setPayments: (state, payload) => {
        const {
            id,
            date,
            value
        } = payload
        const table = state.tables.find(table => table.id === id);
        table.value = table.value - value;

        table.payment.push({
            date: date,
            value: value
        })

    },
    closeTable: (state, payload) => {
        const {
            id
        } = payload
        const table = state.tables.find(table => table.id === id);

        table.hour = '00:00',
            table.value = 0,
            table.payment = [],
            table.order = {
                products: [
                    items
                ]
            }

    }
}

// const actions = {
//     addItem({
//         commit
//     }, item) {
//         commit('pushItem', item);
//     },
// }
// const getters = {
//     tableList: (state) => {
//         return state.items;
//     },
// }


export default new Vuex.Store({
    namespaced: true,
    state,
    mutations
})
