import Vue from 'vue'
import App from './App.vue'
import router from "./router";
import store from './store/store.js'

Vue.config.productionTip = false

Vue.mixin({
  methods: {
    dateNow: function () {
      const date = new Date();
      const fdate =
        date.getDate().lenth == 1 ? '0' + date.getDate() : date.getDate() +
        "/" +
        (date.getMonth() + 1) +
        "/" +
        date.getFullYear() +
        " " +
        date.getHours() +
        ":" +
        date.getMinutes();
      return fdate;
    },
    dateHourNow: function () {
      const date = new Date();
      const hour = date.getHours() + ":" + date.getMinutes();
      return hour;
    },
    weekDay: function () {
      const date = new Date();
      const day = date.getDay();

      switch (day) {
        case 1:
          return 'Segunda-Feira'
        case 2:
          return 'Terça-Feira'
        case 3:
          return 'Quarta-Feira'
        case 4:
          return 'Quinta-Feira'
        case 5:
          return 'Sexta-Feira'
        case 6:
          return 'Sabado'
        case 0:
          return 'Domingo'
      }
    }
  },
})


new Vue({
  router,
  store,
  render: h => h(App),
}).$mount('#app')
