export default {
    dolar: async function (brl) {
        var myHeaders = new Headers();
        myHeaders.append("apikey", "5N5RO16zpqTYbajCvd9WBnB9f5qVC5ye");

        var requestOptions = {
            method: 'GET',
            redirect: 'follow',
            headers: myHeaders
        };

        fetch("https://api.apilayer.com/exchangerates_data/convert?to=USD&from=BRL&amount=" + brl, requestOptions)
            .then(response => response.json())
            .then((result)=>{
                return result.json();
            })
            .catch(error => console.log('error', error));
    }

}
