export default {
    dolar: async function (brl) {
        var myHeaders = new Headers();
        myHeaders.append("apikey", "5N5RO16zpqTYbajCvd9WBnB9f5qVC5ye");

        var requestOptions = {
            method: 'GET',
            redirect: 'follow',
            headers: myHeaders
        };

        const value = await fetch("https://api.apilayer.com/exchangerates_data/convert?to=USD&from=BRL&amount=" + brl, requestOptions)
        return value.json();
    }

}
