//Api in using apilayer.com

export async function convert(to,current,value) {
    var myHeaders = new Headers();
    myHeaders.append("apikey", "5N5RO16zpqTYbajCvd9WBnB9f5qVC5ye");

    var requestOptions = {
        method: 'GET',
        redirect: 'follow',
        headers: myHeaders
    };

    const res = await fetch("https://api.apilayer.com/exchangerates_data/convert?to="+to+"&from="+current+"&amount=" + value, requestOptions);

    const resp = await res.json();
    return resp;
}
