import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

// root store object.
// each Vuex instance is just a single store tree.
const store = {
    table1: {
        id: 1,
        hour: '00:00',
        value: 'R$ 00,00',
    },
    table2: {
        id: 2,
        hour: '00:00',
        value: 'R$ 00,00',
    },
}

// mutations are operations that actually mutate the store.
// each mutation handler gets the entire store tree as the
// first argument, followed by additional payload arguments.
// mutations must be synchronous and can be recorded by plugins
// for debugging purposes.
const mutations = {
    increment(store) {
        store.count++
    },
    decrement(store) {
        store.count--
    }
}

// actions are functions that cause side effects and can involve
// asynchronous operations.
const actions = {
    increment: ({
        commit
    }) => commit('increment'),
    decrement: ({
        commit
    }) => commit('decrement'),
}

// getters are functions.
const getters = {
    evenOrOdd: store => store.count % 2 === 0 ? 'even' : 'odd'
}

// A Vuex instance is created by combining the store, mutations, actions,
// and getters.
export default new Vuex.Store({
    store,
    getters,
    actions,
    mutations
})
